Espo.define('esignature-field:controllers/esignature-document', 'controller', function (Dep) {

    return Dep.extend({

        actionShowDocument: function (options) {
            if (typeof options !== 'object') {
                window.history.back();
            } else {
                // remove the copywright notice while viewing or printing the full page display
                $("#footer").hide();
                // tells the system to display the output generated by the view 'full-page-esignature' in div '#main' 
                this.main('esignature-field:views/full-page-esignature-document', options);
            }
        },

        actionIndex: function (options) {
            alert("No action invoked");
            window.history.back();
        }
    });
});
